mapper.select:
select 

from emp_access
where e_id = #{eId,jdbcType=INTEGER}
  and a_id = #{aId,jdbcType=INTEGER}
  and access_date = #{accessDate,jdbcType=DATE}

mapper.delete:
delete from emp_access
where e_id = #{eId,jdbcType=INTEGER}
  and a_id = #{aId,jdbcType=INTEGER}
  and access_date = #{accessDate,jdbcType=DATE}

mapper.update:
update emp_access


    score = #{score,jdbcType=DOUBLE},


    manager_id = #{managerId,jdbcType=INTEGER},


where e_id = #{eId,jdbcType=INTEGER}
  and a_id = #{aId,jdbcType=INTEGER}
  and access_date = #{accessDate,jdbcType=DATE}

update emp_access
set score = #{score,jdbcType=DOUBLE},
  manager_id = #{managerId,jdbcType=INTEGER}
where e_id = #{eId,jdbcType=INTEGER}
  and a_id = #{aId,jdbcType=INTEGER}
  and access_date = #{accessDate,jdbcType=DATE}
